00052         private static string GetFilterDisplayName(PackageFilter filter)
00053         {
00054             switch (filter)
00055             {
00056                 case PackageFilter.All:
00057                     return "All packages";
00058                 case PackageFilter.Local:
00059                     return "In Project";
00060                 case PackageFilter.Modules:
00061                     return "Built-in packages";
00062                 case PackageFilter.None:
00063                     return "None";
00064                 default:
00065                     return filter.ToString();
00066             }
00067         }
