00069         private void SetFilter(object obj)
00070         {
00071             var previouSelectedFilter = selectedFilter;
00072             selectedFilter = (PackageFilter) obj;
00073             FilterButton.text = string.Format("{0} â–¾", GetFilterDisplayName(selectedFilter));
00074 
00075             if (selectedFilter != previouSelectedFilter)
00076                 PackageCollection.Instance.SetFilter(selectedFilter);
00077         }
