00079         private void OnFilterButtonMouseDown(MouseDownEvent evt)
00080         {
00081             if (evt.propagationPhase != PropagationPhase.AtTarget)
00082                 return;
00083 
00084             var menu = new GenericMenu();
00085             menu.AddItem(new GUIContent(GetFilterDisplayName(PackageFilter.All)), 
00086                 selectedFilter == PackageFilter.All, 
00087                 SetFilter, PackageFilter.All);
00088             menu.AddItem(new GUIContent(GetFilterDisplayName(PackageFilter.Local)), 
00089                 selectedFilter == PackageFilter.Local, 
00090                 SetFilter, PackageFilter.Local);
00091             menu.AddSeparator("");
00092             menu.AddItem(new GUIContent(GetFilterDisplayName(PackageFilter.Modules)), 
00093                 selectedFilter == PackageFilter.Modules, 
00094                 SetFilter, PackageFilter.Modules);
00095             var menuPosition = new Vector2(FilterButton.layout.xMin, FilterButton.layout.center.y);
00096             menuPosition = this.LocalToWorld(menuPosition);
00097             var menuRect = new Rect(menuPosition, Vector2.zero);
00098             menu.DropDown(menuRect);
00099         }
00100 
00101         private void OnAdvancedButtonMouseDown(MouseDownEvent evt)
00102         {
00103             if (evt.propagationPhase != PropagationPhase.AtTarget)
00104                 return;
00105 
00106             var menu = new GenericMenu();
00107 
00108             menu.AddItem(new GUIContent("Show preview packages"), PackageManagerPrefs.ShowPreviewPackages, TogglePreviewPackages);
00109             //menu.AddItem(new GUIContent("Reset to defaults"), false, ResetToDefaultsClick);
00110 
00111             var menuPosition = new Vector2(AdvancedButton.layout.xMax + 30, AdvancedButton.layout.center.y);
00112             menuPosition = this.LocalToWorld(menuPosition);
00113             var menuRect = new Rect(menuPosition, Vector2.zero);
00114             menu.DropDown(menuRect);
00115         }
