00117         private static void TogglePreviewPackages()
00118         {
00119             var showPreviewPackages = PackageManagerPrefs.ShowPreviewPackages;
00120             if (!showPreviewPackages && PackageManagerPrefs.ShowPreviewPackagesWarning)
00121             {
00122                 const string message = "Preview packages are not verified with Unity, may be unstable, and are unsupported in production. Are you sure you want to show preview packages?";
00123                 if (!EditorUtility.DisplayDialog("Unity Package Manager", message, "Yes", "No"))
00124                     return;
00125                 PackageManagerPrefs.ShowPreviewPackagesWarning = false;
00126             }
00127             PackageManagerPrefs.ShowPreviewPackages = !showPreviewPackages;
00128             PackageCollection.Instance.UpdatePackageCollection(true);
00129         }
