00131         private void ResetToDefaultsClick()
00132         {
00133             if (!EditorUtility.DisplayDialog("Unity Package Manager", "Operation will reset all your packages to Editor defaults. Do you want to continue?", "Yes", "No"))
00134                 return;
00135 
00136             // Registered on callback
00137             AssemblyReloadEvents.beforeAssemblyReload += PackageManagerWindow.ShowPackageManagerWindow;
00138 
00139             Client.ResetToEditorDefaults();
00140 
00141             var windows = UnityEngine.Resources.FindObjectsOfTypeAll<PackageManagerWindow>();
00142             if (windows.Length > 0)
00143             {
00144                 windows[0].Close();
00145             }
00146         }
